package com.sweet.config;import com.baomidou.mybatisplus.extension.plugins.PaginationInterceptor;import org.mybatis.spring.annotation.MapperScan;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configuration@MapperScan(basePackages = {"com.sweet.modular.*.mapper","com.sweet.system.mapper"})public class MybatisPlusConfig {    /**     * mybatis-plus 分页插件     * @return     */    @Bean    public PaginationInterceptor paginationInterceptor(){        return new PaginationInterceptor();    }}