package com.sweet.core.exception.enums;public enum SystemExceptionEnum implements AbstractBaseExceptionEnum {    SQL_EXCEPTION(500,"数据异常，请联系管理员！"),    UNKNOWN_ACCOUNT_EXCEPTION(500,"该用户不存在！"),    LOGIN_FAILURE_EXCEPTION(401,"登录状态已失效！"),    NO_PERMISSION_EXCEPTION(500,"抱歉您没有访问权限！");    ;    SystemExceptionEnum(int code, String message) {        this.code = code;        this.message = message;    }    private Integer code;    private String message;    @Override    public Integer getCode() {        return code;    }    public void setCode(Integer code) {        this.code = code;    }    @Override    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }}