package com.sweet.core.mybatis;import com.baomidou.mybatisplus.core.handlers.MetaObjectHandler;import org.apache.ibatis.reflection.MetaObject;import org.springframework.context.annotation.Configuration;import javax.sound.midi.MetaEventListener;import java.time.LocalDateTime;import java.util.Date;@Configurationpublic class MybatisObjectHandler implements MetaObjectHandler {    @Override    public void insertFill(MetaObject metaObject) {        setFieldValByName("createTime", new Date(),metaObject);    }    @Override    public void updateFill(MetaObject metaObject) {        setFieldValByName("modifyTime",new Date(),metaObject);    }}