package com.sweet.core.shiro;import java.util.ArrayList;import java.util.List;import static java.util.stream.Collectors.toList;public class Test {    public static void main(String[] args) {        List<Person> list = new ArrayList<>();        list.add(new Person("jack", 20));        list.add(new Person("mike", 25));        list.add(new Person("tom", 30));        List<String> newlist = list.stream().map(Person::getName).collect(toList());        System.out.println(newlist);    }}