package com.sweet.core.shiro.session;import com.sweet.core.model.ResultBean;import com.sweet.core.shiro.UserOnline;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controller@RequestMapping("/admin/online")public class SessionController {    @Autowired    SessionService sessionService;    @RequestMapping("")    public String online() {        return "/system/online/online";    }    @ResponseBody    @RequestMapping("/list")    public ResultBean list() {        List<UserOnline> list = sessionService.list();        return ResultBean.success(list);    }}