package com.sweet.core.shiro.session;import com.sweet.core.shiro.UserOnline;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controller@RequestMapping("/online")public class SessionController {    @Autowired    SessionService sessionService;    @RequestMapping("index")    public String online() {        return "online";    }    @ResponseBody    @RequestMapping("list")    public List<UserOnline> list() {        return sessionService.list();    }}