package com.sweet.modular.account.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.account.entity.Account;import com.sweet.modular.account.service.AccountService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 开发账号 前端控制器 * </p> * * @author admin * @since 2020-01-10 */@Controller@RequestMapping("/admin/account")public class AccountController {    @Autowired    AccountService accountService;    /**     * 列表页     */    @RequestMapping("/account_list")    public String list(){        return "/modular/account/account_list";    }    /**     * 编辑页     */    @RequestMapping("/account_edit")    public String edit(){        return "/modular/account/account_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editAccount")    @ResponseBody    public ResultBean editAccount(Account account){        accountService.saveOrUpdate(account);        return ResultBean.success(account);    }    /**     * 删除     */    @RequestMapping("/delAccount")    @ResponseBody    public ResultBean delAccount(Account account){        accountService.removeById(account);        return ResultBean.success(account);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getAccountDetail")    @ResponseBody    public ResultBean getAccountDetail(String id){        Account account = accountService.getById(id);        return ResultBean.success(account);    }    /**     * 列表数据     */    @RequestMapping("/getAccountList")    @ResponseBody    @TranslationDict({    })    public LayuiPageInfo getAccountList(Account Account){        LayuiPageInfo pageInfo = accountService.findPageBySpec(Account);        return pageInfo;    }}