package com.sweet.modular.account.service.impl;import com.sweet.modular.account.entity.Account;import com.sweet.modular.account.mapper.AccountMapper;import com.sweet.modular.account.service.AccountService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 开发账号 服务实现类 * </p> * * @author admin * @since 2020-01-10 */@Servicepublic class AccountServiceImpl extends ServiceImpl<AccountMapper, Account> implements AccountService {    @Override    public LayuiPageInfo findPageBySpec(Account account) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, account);        return LayuiPageFactory.createPageInfo(page);    }}