package com.sweet.modular.article.mapper;import com.sweet.modular.article.entity.Article;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * <p> * 文章 Mapper 接口 * </p> * * @author admin * @since 2020-01-14 */public interface ArticleMapper extends BaseMapper<Article> {    Page<Article> customPageList(Page page, @Param("param") Article article);    @Select("select content from business_article where article_id = #{articleId}")    String getArticltContent(@Param("articleId") String articleId);}