package com.sweet.modular.blog.controller;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import com.sweet.modular.article.entity.Article;import com.sweet.modular.article.service.ArticleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controller@RequestMapping("/blog")public class BlogController {    @Autowired    ArticleService articleService;    @RequestMapping("")    public String index(){        return "/modular/blog/index";    }    @RequestMapping("/article")    public String article(){        return "/modular/blog/article";    }    @RequestMapping("/detail")    public String detail(){        return "/modular/blog/detail";    }    /**     * 列表数据     */    @RequestMapping("/getArticleList")    @ResponseBody    public ResultBean getArticleList(Article Article){        LayuiPageInfo pageInfo = articleService.findPageBySpec(Article);        return ResultBean.success(pageInfo);    }    /**     * 列表数据     */    @RequestMapping("/getArticleDetail")    @ResponseBody    public ResultBean getArticleList(String articleId){        String content = articleService.getArticltContent(articleId);        return ResultBean.success(content);    }}