package com.sweet.modular.student.controller;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.student.entity.Student;import com.sweet.modular.student.service.StudentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 用户表 前端控制器 * </p> * * @author admin * @since 2019-12-17 */@Controller@RequestMapping("/admin/student")public class StudentController {    @Autowired    StudentService studentService;    /**     * 列表页     */    @RequestMapping("/student_list")    public String list(){        return "/modular/student/student_list";    }    /**     * 编辑页     */    @RequestMapping("/student_edit")    public String edit(){        return "/modular/student/student_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editStudent")    @ResponseBody    public ResultBean editStudent(Student student){        studentService.saveOrUpdate(student);        return ResultBean.success(student);    }    /**     * 删除     */    @RequestMapping("/delStudent")    @ResponseBody    public ResultBean delStudent(Student student){        studentService.removeById(student);        return ResultBean.success(student);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getStudentDetail")    @ResponseBody    public ResultBean getStudentDetail(String studentId){        Student student = studentService.getById(studentId);        return ResultBean.success(student);    }    /**     * 列表数据     */    @RequestMapping("/getStudentList")    @ResponseBody    public LayuiPageInfo getStudentList(Student Student){        LayuiPageInfo pageInfo = studentService.findPageBySpec(Student);        return pageInfo;    }}