package com.sweet.modular.student.service.impl;import com.sweet.modular.student.entity.Student;import com.sweet.modular.student.mapper.StudentMapper;import com.sweet.modular.student.service.StudentService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 用户表 服务实现类 * </p> * * @author admin * @since 2019-12-17 */@Servicepublic class StudentServiceImpl extends ServiceImpl<StudentMapper, Student> implements StudentService {    @Override    public LayuiPageInfo findPageBySpec(Student student) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, student);        return LayuiPageFactory.createPageInfo(page);    }}