package com.sweet.modular.test.service.impl;import com.sweet.modular.test.entity.Test;import com.sweet.modular.test.mapper.TestMapper;import com.sweet.modular.test.service.TestService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 用户表 服务实现类 * </p> * * @author admin * @since 2019-12-11 */@Servicepublic class TestServiceImpl extends ServiceImpl<TestMapper, Test> implements TestService {    @Override    public LayuiPageInfo findPageBySpec(Test test) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, test);        return LayuiPageFactory.createPageInfo(page);    }}