package com.sweet.modular.testStu.service.impl;import com.sweet.modular.testStu.entity.TestStu;import com.sweet.modular.testStu.mapper.TestStuMapper;import com.sweet.modular.testStu.service.TestStuService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 用户表 服务实现类 * </p> * * @author admin * @since 2019-12-17 */@Servicepublic class TestStuServiceImpl extends ServiceImpl<TestStuMapper, TestStu> implements TestStuService {    @Override    public LayuiPageInfo findPageBySpec(TestStu testStu) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, testStu);        return LayuiPageFactory.createPageInfo(page);    }}