package com.sweet.modular.wx;import cn.hutool.json.JSON;import cn.hutool.json.JSONUtil;import com.soecode.wxtools.api.IService;import com.soecode.wxtools.api.WxService;import com.soecode.wxtools.bean.WxMenu;import com.soecode.wxtools.exception.WxErrorException;import com.sweet.core.model.ResultBean;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;@Controller@RequestMapping("/admin/wxmp")public class WxMpController {    IService iService = new WxService();    @RequestMapping("")    public String index(){        return "/modular/wx/index";    }    @RequestMapping("/updateMenu")    @ResponseBody    public ResultBean updateMenu(String menu) throws WxErrorException {        System.out.println(menu);        WxMenu wxMenu = JSONUtil.toBean(menu, WxMenu.class);        iService.createMenu(wxMenu,false);        return ResultBean.success();    }}