package com.sweet.system.controller;import com.sweet.core.shiro.ShiroKit;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMapping("/admin")public class AdminController {    @RequestMapping("")    public String index(Model model){        model.addAttribute("shiroUser",ShiroKit.getUser());        return "/system/index";    }}