package com.sweet.system.service.impl;import com.sweet.system.entity.DictType;import com.sweet.system.mapper.DictTypeMapper;import com.sweet.system.service.DictTypeService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 字典类型表 服务实现类 * </p> * * @author admin * @since 2019-12-16 */@Servicepublic class DictTypeServiceImpl extends ServiceImpl<DictTypeMapper, DictType> implements DictTypeService {    @Override    public LayuiPageInfo findPageBySpec(DictType dictType) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, dictType);        return LayuiPageFactory.createPageInfo(page);    }}