package com.sweet.wx.handler;import com.soecode.wxtools.api.IService;import com.soecode.wxtools.api.WxMessageHandler;import com.soecode.wxtools.bean.WxXmlMessage;import com.soecode.wxtools.bean.WxXmlOutMessage;import com.soecode.wxtools.bean.WxXmlOutNewsMessage;import com.soecode.wxtools.exception.WxErrorException;import com.sweet.wx.wxconst.ResponseConst;import java.util.Map;public class ArticleHandler implements WxMessageHandler {    @Override    public WxXmlOutMessage handle(WxXmlMessage wxMessage, Map<String, Object> context, IService iService) throws WxErrorException {        return execute(wxMessage);    }    private WxXmlOutMessage execute(WxXmlMessage wxMessage) {        WxXmlOutNewsMessage.Item item = new WxXmlOutNewsMessage.Item();        item.setTitle("[赛后]尼克斯87-117湖人，詹姆斯31+5+5，浓眉伤退，巴雷特19+5，湖人六连胜");        item.setUrl("https://bbs.hupu.com/31692179.html");        item.setDescription("虎扑1月8日讯 近期状态出色的湖人今日迎来尼克斯的挑战，首节比赛两支球队状态相当，湖人直到节末真正确立起领先优势，浓眉哥最后时刻连投带罚为湖人取得5分领先结束第一节。次节湖人迎来大爆发，他们末段下起三分雨成功打出净胜两位数的攻势，半场结束时，湖人62-45领先尼克斯。");        item.setPicUrl("https://c1.hoopchina.com.cn/uploads/star/event/images/200108/3190c8dd53289d23c94f0b2f0e9e431bd2f4ee22.jpg");        return WxXmlOutMessage.NEWS().addArticle(item).toUser(wxMessage.getFromUserName()).fromUser(wxMessage.getToUserName()).build();    }}