package com.sweet.wx.handler;import com.soecode.wxtools.api.IService;import com.soecode.wxtools.api.WxMessageHandler;import com.soecode.wxtools.bean.WxXmlMessage;import com.soecode.wxtools.bean.WxXmlOutMessage;import com.soecode.wxtools.exception.WxErrorException;import com.sweet.wx.wxconst.ResponseConst;import java.util.Map;public class HelloHandler implements WxMessageHandler {    @Override    public WxXmlOutMessage handle(WxXmlMessage wxXmlMessage, Map<String, Object> map, IService iService) throws WxErrorException {        return execute(wxXmlMessage);    }    private WxXmlOutMessage execute(WxXmlMessage wxMessage) {        return WxXmlOutMessage.TEXT().content(ResponseConst.DEFAULE_TEXT).toUser(wxMessage.getFromUserName()).fromUser(wxMessage.getToUserName()).build();    }}