package com.sweet.wx.handler;import com.soecode.wxtools.api.IService;import com.soecode.wxtools.api.WxMessageHandler;import com.soecode.wxtools.bean.WxXmlMessage;import com.soecode.wxtools.bean.WxXmlOutMessage;import com.soecode.wxtools.exception.WxErrorException;import com.sweet.wx.wxconst.ResponseConst;import java.util.Map;public class HelpDocHandler implements WxMessageHandler {    @Override    public WxXmlOutMessage handle(WxXmlMessage wxMessage, Map<String, Object> context, IService iService) throws WxErrorException {        WxXmlOutMessage response = execute(wxMessage);        return response;    }    private WxXmlOutMessage execute(WxXmlMessage wxMessage) {        return WxXmlOutMessage.TEXT().content(ResponseConst.HELP).toUser(wxMessage.getFromUserName()).fromUser(wxMessage.getToUserName()).build();    }}