package cn.stylefeng.guns.modular.wx.matcher;import com.soecode.wxtools.api.WxMessageMatcher;import com.soecode.wxtools.bean.WxXmlMessage;import com.sweet.core.util.StringUtil;public class WhoAmIMatcher implements WxMessageMatcher {    @Override    public boolean match(WxXmlMessage message) {        if(!StringUtil.isEmpty(message.getContent())){            if(message.getContent().equals("我是谁")){                return true;            }        }        return false;    }}