package com.sweet.wxmp.util;import com.sweet.wxmp.WxMpProperties;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;@Componentpublic class WxMpUtil {    @Autowired    WxMpProperties properties;    private static String appId ;    @PostConstruct    public void transValues() {        appId = properties.getConfigs().get(1).getAppId();    }    public static final String buildWxUrl(String url){        String CODE="https://open.weixin.qq.com/connect/oauth2/authorize?appid=APPID&redirect_uri=REDIRECT_URI&response_type=code&scope=SCOPE&state=STATE#wechat_redirect";        return CODE.replace("APPID",appId).replace("REDIRECT_URI", url).replace("SCOPE", "snsapi_userinfo").replace("STATE", "jkdn");    }}